package main

import (
	"github.com/noelyahan/mergi"
	"github.com/noelyahan/impexp"
	"image"
	"log"
	"fmt"
)

// Mergi supports url to image, Lets try to merge this placeholders
func main() {
	// Opps Sorry ! let's ignore error for now
	img1, _ := mergi.Import(impexp.NewURLImporter("https://via.placeholder.com/500x500"))

	img2, _ := mergi.Import(impexp.NewBase64Importer("data:image/jpg;base64,"))
	img3, _ := mergi.Import(impexp.NewURLImporter("https://via.placeholder.com/250x250"))

	img4, _ := mergi.Import(impexp.NewURLImporter("https://via.placeholder.com/100x100"))
	img5, _ := mergi.Import(impexp.NewURLImporter("https://via.placeholder.com/100x100"))
	img6, _ := mergi.Import(impexp.NewURLImporter("https://via.placeholder.com/100x100"))
	img7, _ := mergi.Import(impexp.NewURLImporter("https://via.placeholder.com/100x100"))
	img8, _ := mergi.Import(impexp.NewURLImporter("https://via.placeholder.com/100x100"))

	imgs := []image.Image{img1, img2, img3, img4, img5, img6, img7, img8}

	res, err := mergi.Merge("TTBTBBBB", imgs)
	if err != nil {
		log.Fatal(err)
	}
	anim, _ := mergi.Animate([]image.Image{img1, img2}, 20)

	// export image file png/jpg
	mergi.Export(impexp.NewFileExporter(res, "examples/import_export/res/out.png"))
	mergi.Export(impexp.NewBase64Exporter("png", res, func(data string) {
		fmt.Println(data)
	}))

	// Export animation gif
	mergi.Export(impexp.NewAnimationExporter(anim, "examples/import_export/res/out.gif"))
}
